import React from 'react';
import { Scene, Router, Actions } from 'react-native-router-flux';

import LoginForm from './components/LoginForm';
import EmployeeCreate from './components/EmployeeCreate';
import EmployeeEdit from './components/EmployeeEdit';
import EmployeeList from './components/EmployeeList';

const RouterComponent = () => {
  return (
    <Router>
      <Scene key="root" hideNavBar titleStyle={{ flex: 1, textAlign: 'center' }}>
        <Scene key="auth">
          <Scene

            key="login"
            component={LoginForm}
            title="Please Login"
            initial
          />
        </Scene>

        <Scene key="main">
          {/* pass props to customize scene: rightTitle, onRight --> Text 'Right' appears in right of Header */}
          {/* Whatever is first will be showed first. If you want, add the initial prop */}
          <Scene
            rightTitle="Add"
            onRight={() => {
              // To navigate, use Actions.keyofthesceneiwanttonavigate
              Actions.employeeCreate();
            }}
            key="employeeList"
            component={EmployeeList}
            title="Employees"
            initial
          />

          <Scene
            key="employeeCreate"
            component={EmployeeCreate}
            title="Create Employee"
          />

          <Scene
            key="employeeEdit"
            component={EmployeeEdit}
            title="Edit Employee"
          />
        </Scene>
      </Scene>
    </Router>
  );
};

export default RouterComponent;
